<?php 

// import file function.php untuk mengambil function-function yang telah dibuat
include "./function.php";

// Cek apakah file tasks.json tersedia
if (file_exists("./tasks.json")) {
  // Mengambil data dari file tasks.json dan convert file JSON menjadi Array Asosiatif
  $tasks = getJsonContent("./tasks.json");

  // reverse array agar task yang terbaru berada di urutan atas
  $tasks = array_reverse($tasks);
} else {
  // Jika file tasks.json tidak tersedia maka isi dengan array kosong
  $tasks = [];
}

